mapper.update:
UPDATE rehab_records
SET
date = #{date},
content = #{content},
barthel_index = #{barthelIndex}
WHERE id = #{id}

mapper.select:
SELECT AVG(barthel_index)
FROM rehab_records
WHERE patient_id = #{patientId}
AND DATE_FORMAT(date, '%Y-%m') = #{yearMonth}

mapper.update:
UPDATE rehab_records
SET barthel_index = NULL
WHERE id = #{recordId}
